import { useState } from "react"

export const Hamburger = () => {

    let [isActive, setIsActive] = useState(false)

    const clicked = () => {
        setIsActive(!isActive)
    }

    return (
        <>
            <button className="hamburger" onClick={clicked}>
                <div><i className="far fa-caret-square-down"></i></div>
            </button>
            {isActive && <div>
                <div className="hamburger-item"><a href='#'>Начало</a></div>
                <div className="hamburger-item"><a href='#'><i className="fas fa-utensils"></i> МЕНЮ</a></div>
                <div className="hamburger-item"><a href='#'>За нас</a></div>
                <div className="hamburger-item">hhkjhkjh</div>
                <div className="hamburger-item">hjkhjhk</div>
            </div>}
        </>
    )
}
