import { useEffect, useState } from "react"
import { Articles } from "./Articles";
import { pics } from "./Pictures"

export const Filter = ({ nameEn, nameBg, onRenderHandler}) => {

    let [isPicked, setPick] = useState(false);

    function onFilter(e) {
        if(e.target.tagName == 'SPAN'){
            return
        }

        useEffect(()=>{
            
        })
        setPick(true)
        onRenderHandler(e)
    }
    
    return (
        <li 
            key={nameEn}
            className={isPicked ? 'filter picked' : 'filter'}
            section-name={nameEn} 
            onClick={onFilter}
        >
            <img src={pics[nameEn]} alt={nameEn} />
            <span className='text'> {nameBg} </span>
        </li>
    )
}
