import { useEffect, useState } from "react"
import { pics } from "./Pictures"

export const Filter = ({data, name})=>{

    let [isPicked, setPick] = useState(false);
    
    function onFilter(){
        setPick(old => false)
        setPick(!isPicked)
    }

    return (
        <li key={data} className={isPicked ? 'filter picked' : 'filter'} data={data} onClick={onFilter}>
            <img src={pics[data]} alt={data} />
            <span className='text'> {name} </span>
        </li>
    )
}
