const sendPurchase = (data) => {

    data.append('service_id', 'service_syyanqm');
    data.append('template_id', 'template_w9lnl65');
    data.append('user_id', 'xQ5PcMNu0l7_KcNZK');
    data.append('Content-Type', 'multipart/form-data');

    fetch('https://api.emailjs.com/api/v1.0/email/send-form', {
        method: 'POST',
        body: data,
        contentType: false,
        processData: false
    })
        .then(response => {
            console.log(data);
        if (response.ok) {
            alert('Вашата поръчка беше направена успешно!')
        } else {
            throw new Error()
        }
    })
    .catch(error => alert('Упс... Възникна грешка... Моля опитайте отново по-късно!'))
}
