import { useState } from "react"

export const Hamburger = () => {

    let [isActive, setIsActive] = useState(false)

    return (
        <>
            <button className="hamburger" type="button" onClick={setIsActive(!isActive)}>
                <div><i className="far fa-caret-square-down"></i></div>
                { isActive && 
                    <>
                        <a href='#'>Начало</a>
                        <a href='#'><i className="fas fa-utensils"></i> МЕНЮ</a>
                        <a href='#'>За нас</a>
                        <a href="tel:0876442740"><i className="fas fa-phone"></i> 0876 442 740</a>
                        <a href="mailto:priateli@abv.bg"><i className="fas fa-at"></i> priateli@abv.bg</a>
                    </>    
                }
            </button>
        </>
    )
}
