import { useEffect, useState } from "react"
import { Articles } from "./Articles"
import { Filter } from "./Filter"

export const MenuFilter = () => {
    let data = []
    let dataType = [
        { en: 'salati', bg: 'салати' },
        { en: 'kartifeni', bg: 'картофи' },
        { en: 'bezmesni', bg: 'безмесни' },
        { en: 'pileshkaSkara', bg: 'пилешка скара' },
        { en: 'svinskaSkara', bg: 'свинска скара' },
        { en: 'pileshkiMezeta', bg: 'пилешки мезета' },
        { en: 'svinskiMezeta', bg: 'свински мезета' },
        { en: 'pileshkiQstiq', bg: 'пилешки ястия' },
        { en: 'svinskiQstiq', bg: 'свински ястия' },
        { en: 'sachove', bg: 'сачове' },
        { en: 'spageti', bg: 'спагети' },
        { en: 'rizoto', bg: 'ризото' },
        { en: 'garnituri', bg: 'гарнитури и сосчета' },
        { en: 'pitki', bg: 'питки и пърленки' },
        { en: 'mezeta', bg: 'мезета' },
        { en: 'deserti', bg: 'десерти' },]

    useEffect(() => {
        fetch("https://parseapi.back4app.com/classes/Menu", {
            method: 'GET',
            headers: {
                "X-Parse-Application-Id": "H6D4cPNXrQlE6Td0u98vAHT4d36CcYbQG9rooOHV",
                "X-Parse-REST-API-Key": "MZnFsyXwYEOVhe4sIiPdn611hVpgTQtuV68Thldj"
            },
            redirect: 'follow'
        })
            .then(response => response.json())
            .then(result => {

                result.results.forEach(x => data.push(x))
            })
            .catch(err => alert(err));
    }, [])
    
    let [info, setInfo] = useState(data)

    return (
        <>
            <div className='menuFilter'>
                <ul>
                    {dataType.map(x => <Filter data={x.en} name={x.bg} />)}
                </ul>
            </div>
            {data.map(x => <Articles info={info} />)}
        </>
    )
}
