import brand from '../img/brand.png';
import { useEffect, useState } from "react";
import { Hamburger } from './Hamburger';

export const Header = () => {
    let [ham, setHam] = useState(false)

    useEffect(()=>{
        let width = window.innerWidth
        if(width < 900){
            setHam(true)
        } else{
            setHam(false)
        }
    },[]);

    window.addEventListener()

    return (
        <section className="header">
            <nav className="navBar">
                <ul className='navUl'>
                    <li className='navItem'>
                        <a href="#" className="brand" /><img src={brand} alt='logo' />
                    </li>
                    <li>{ham && <Hamburger />}</li>
                    {!ham &&
                        <>
                            <li className='navItem'>
                                <a href='#'>Начало</a>
                            </li>
                            <li className='navItem'>
                                <a href='#'><i className="fas fa-utensils"></i> МЕНЮ</a>
                            </li>
                            <li className='navItem'>
                                <a href='#'>За нас</a>
                            </li>
                            <li className='navItem'>
                                <a href="tel:0876442740"><i className="fas fa-phone"></i> 0876 442 740</a>
                            </li>
                            <li className="navItem">
                                <a href="mailto:priateli@abv.bg"><i className="fas fa-at"></i> priateli@abv.bg</a>
                            </li>
                        </>
                    }
                    <li className='navItem'>
                        <a href='#'><i className="fas fa-shopping-cart"></i> Количка</a>
                    </li>
                </ul>
            </nav>
        </section>
    )
}
