import { useEffect, useState } from "react"
import { CartItems } from "./CartItems"

export const Cart = ({itemForCart, data, deleteFromCart})=>{

    if(itemForCart.length != 0 || itemForCart.isRendered){
        return (
            <section className="cart">
                <h4>Количка</h4>
                <div className="headers">
                    <div className="cart-item">Продукти</div>
                    <div className="cart-price">Цена</div>
                    <div className="cart-quantity">Количество</div>
                </div>
                    {Array.from(itemForCart).map((x,i) => x.isRendered && <CartItems key={i} info={x} data={data} deleteFromCart={deleteFromCart}/>)}
            </section>
        )
    }   
}
