import { useEffect, useRef } from "react";

export const useSendClientDataFetch = (clientData) => {
    let isMounted = useRef(false)
    console.log(isMounted);
    
    useEffect(() => {
        if(isMounted){
            fetch("https://parseapi.back4app.com/classes/clientData", {
                method: 'POST',
                headers: {
                    "X-Parse-Application-Id" : "H6D4cPNXrQlE6Td0u98vAHT4d36CcYbQG9rooOHV",
                    "X-Parse-REST-API-Key" : "MZnFsyXwYEOVhe4sIiPdn611hVpgTQtuV68Thldj",
                    "Content-Type" : "application/json"},
                body: JSON.stringify({
                    "firstName": clientData.firstName,
                    "lastName": clientData.lastName,
                    "tel": clientData.tel,
                    "address": clientData.address,
                    "addressNumber": clientData.addressNumber,
                    "purchase": clientData.purchase
            }),
            redirect: 'follow'
            })
                .then(response => response.text())
                .then(result => console.log(result))
                .catch(error => alert('error', error));
        }
        
    }, [clientData])
    isMounted.current = true;
}
