import brand from '../img/brand.png';
import { useEffect, useState } from "react";
import { Hamburger } from './header/Hamburger';
import { NavbarItems } from './header/NavbarItems'

export const Header = ({itemsInCartCounter}) => {
    let [ham, setHam] = useState(false)
    let [index ,setIndex] = useState(0)
    let width = window.innerWidth;
    let [shake, setShake] = useState(false)


    useEffect(()=>{
        setIndex(itemsInCartCounter)
        setShake(!shake)
    },[itemsInCartCounter])
    
    useEffect(()=>{
        if (width < 900) {
            setHam(true)
        } else {
            setHam(false)
        }
    },[])

    window.addEventListener('resize', ()=>{
        width = window.innerWidth
        if (width < 900) {
            setHam(true)
        } else {
            setHam(false)
        }
    })

    return (
        <section className="header">
            <nav className="navBar">
                <ul className='navUl'>
                    <li className='navItem'>
                        <a href="#" className="brand" /><img src={brand} alt='logo' />
                    </li>
                    {ham && <Hamburger />}
                    {!ham && <NavbarItems />}
                    <li className='navItem'>
                        <a href='#' className={shake? "cart-icon shake": "cart"}><i className="fas fa-shopping-cart"></i> Количка<i className="index">{index}</i></a>
                    </li>
                </ul>
            </nav>
        </section>
    )
}
