import { useEffect, useState } from "react"
import { Articles } from "./Articles";
import { pics } from "./Pictures"

export const Filter = ({ nameEn, nameBg, isPicked, onClick}) => {



    return (
        <li key={nameEn} className={isPicked ? 'filter picked' : 'filter'} section-name={nameEn} onClick={onClick} on>
            <img src={pics[nameEn]} alt={nameEn} />
            <span className='text'> {nameBg} </span>
        </li>
    )
}
