import brand from '../img/brand.png';
import { useEffect, useState } from "react";
import { Hamburger } from './Hamburger';
import { NavbarItems } from './header/NavbarItems'

export const Header = () => {
    let [ham, setHam] = useState(false)

    let width = window.innerWidth;

    



    return (
        <section className="header">
            <nav className="navBar">
                <ul className='navUl'>
                    <li className='navItem'>
                        <a href="#" className="brand" /><img src={brand} alt='logo' />
                    </li>
                    <li>{width<900 && <Hamburger />}</li>
                    {width >=900 && <NavbarItems />}
                    <li className='navItem'>
                        <a href='#'><i className="fas fa-shopping-cart"></i> Количка</a>
                    </li>
                </ul>
            </nav>
        </section>
    )
}
