import { useEffect, useState } from "react"
import { Articles } from "./Articles"
import { Filter } from "./Filter"

export const MenuFilter = ({ dataType }) => {

    let [isPicked, setPick] = useState(false);

    function onFilter(e) {
        e.parentElement.className = 'filter picked'
        set
    }

    return (
        <div className='menu-filter'>
            <ul>
                {dataType.map(x => <Filter
                    key={x.en}
                    nameEn={x.en}
                    nameBg={x.bg}
                    isPicked={isPicked}
                    handler={onFilter} />
                )}
            </ul>
        </div>
    )
}
