import { useState, useRef } from "react"
import { pics } from "../Pictures"

export const Filter = ({ nameEn, nameBg, onRenderHandler }) => {
    const pick = useRef();

    let [isPicked, setPick] = useState(false);

    function onFilter(e) {
        console.log(pick.current);
        [...e.target.parentElement.parentElement.children].forEach(x => x.className = 'filter')

        if (e.target.tagName === 'SPAN' || e.target.tagName === 'LI') {
            return;
        }
        setPick(true);
        e.target.parentElement.className = 'filter picked'
        onRenderHandler(nameEn);
    }

    return (
        <li
        ref={pick}
            key={nameEn}
            className={isPicked ? 'filter picked' : 'filter'}
            section-name={nameEn}
            onClick={onFilter}
        >
            <img src={pics[nameEn]} alt={nameEn} ref={pick}/>
            <span className='text'> {nameBg} </span>
        </li>
    )
}
