import { Articles } from "./Articles"
import { MenuFilter } from "./MenuFilter"
import { useEffect, useState } from "react"
import { Menu } from "./Menu"


export const MenuSection = ({addToCartClickHandle})=>{

    let dataType = [
        { en: 'salati', bg: 'салати' },
        { en: 'kartofeni', bg: 'картофи' },
        { en: 'bezmesni', bg: 'безмесни' },
        { en: 'pileshkaSkara', bg: 'пилешка скара' },
        { en: 'svinskaSkara', bg: 'свинска скара' },
        { en: 'pileshkiMezeta', bg: 'пилешки мезета' },
        { en: 'svinskiMezeta', bg: 'свински мезета' },
        { en: 'pileshkiQstiq', bg: 'пилешки ястия' },
        { en: 'svinskiQstiq', bg: 'свински ястия' },
        { en: 'sachove', bg: 'сачове' },
        { en: 'spageti', bg: 'спагети' },
        { en: 'rizoto', bg: 'ризото' },
        { en: 'garnituri', bg: 'гарнитури и сосчета' },
        { en: 'pitki', bg: 'питки и пърленки' },
        { en: 'mezeta', bg: 'мезета' },
        { en: 'deserti', bg: 'десерти' },]
    
    let [data, setData] = useState([]);
        
    useEffect(() => {
        fetch("https://parseapi.back4app.com/classes/Menu", {
            method: 'GET',
            headers: {
                "X-Parse-Application-Id": "H6D4cPNXrQlE6Td0u98vAHT4d36CcYbQG9rooOHV",
                "X-Parse-REST-API-Key": "MZnFsyXwYEOVhe4sIiPdn611hVpgTQtuV68Thldj"
            },
            redirect: 'follow'
        })
            .then(response => response.json())
            .then(result => setData(result.results))
            .catch(error => alert(error));
    }, [])

    let [result, setResult] = useState()

    const onRenderHandler = (e)=>{
        setResult(e.target.alt)
    }

    return(
        <section className='menu'>
            <h2>МЕНЮ</h2>
            <MenuFilter dataType={dataType} onRenderHandler={onRenderHandler}/>
            <Menu data={data} result={result} addToCartClickHandle/>
        </section>
        
    )
}
