import './App.css'
import { Header } from "./components/Header";
import { BackgroundPicture } from "./components/BackgroundPicture";
import { MenuSection } from './components/MenuSection';
import { Cart } from './components/Cart';
import { useEffect, useState, useContext } from 'react';
import DataContext from './components/DataContext'

function App() {
  let [data, setData] = useState([])
  let [itemForCart, setItemForCart] = useState([])

  useEffect(() => {
    fetch("https://parseapi.back4app.com/classes/Menu", {
      method: 'GET',
      headers: {
        "X-Parse-Application-Id": "H6D4cPNXrQlE6Td0u98vAHT4d36CcYbQG9rooOHV",
        "X-Parse-REST-API-Key": "MZnFsyXwYEOVhe4sIiPdn611hVpgTQtuV68Thldj"
      },
      redirect: 'follow'
    })
      .then(response => response.json())
      .then(result => setData(result.results))
      .catch(error => alert(error));
  }, [])

  const addToCartClickHandler = (info) => {
    info.isRendered = true;
    setItemForCart(oldArr => [...oldArr, info]);
  }

  const deleteFromCart = (currentElement) => {
    currentElement.isRendered = false
    setItemForCart(oldArr => [...itemForCart.filter(x=> x.isRendered == true)])
  }

  return (
    <>
      <DataContext.Provider data={data}>
        <Header />
        <BackgroundPicture />
        <Cart itemForCart={itemForCart} deleteFromCart={deleteFromCart} />
        <MenuSection addToCartClickHandler={addToCartClickHandler} data={data} />
      </DataContext.Provider>
    </>
  );
}

export default App;
