import { useEffect, useState } from "react"
import { pics } from "./Pictures"

export const Filter = (props)=>{
    console.log(props);
    let [isPicked, setPick] = useState(false);
    
    function onFilter(){
        setPick(!isPicked)
    }

    return (
        <li key={props.data} className={isPicked ? 'filter picked' : 'filter'} data={props.data} onClick={onFilter}>
            <img src={pics[props.data]} alt={props.data} />
            <span className='text'> {props.name} </span>
        </li>
    )
}
