import { useEffect, useState } from "react"
import { CartItems } from "./CartItems"

export const Cart = ({itemForCart, data})=>{



    const deleteElement = (currentElement) => {
        let find = itemForCart.find(x=> x.objectId == currentElement.objectId)
        console.log(find);
    }

    if(itemForCart.length != 0){
        return (
            <section className="cart">
                <h4>Количка</h4>
                <div className="headers">
                    <div className="cart-item">Продукти</div>
                    <div className="cart-price">Цена</div>
                    <div className="cart-quantity">Количество</div>
                </div>
                    {Array.from(itemForCart).map((x,i) => <CartItems key={i} info={x} data={data} deleteElement={deleteElement}/>)}
            </section>
        )
    }   
}
