export const CartItems = ({info, data, deleteElement }) => {

    let name = info.childNodes[0].textContent;
    // let price = Number(info.childNodes[2].firstChild.data);

    const curentElement = data.find( x=> x.name == name);

    console.log(curentElement);

    const deleteItem = () => {
        deleteElement(curentElement)
    }

    return (
        <div className="item">
            <div className="title">{curentElement.name}</div>
            <div className="price">{curentElement.price}</div>
            <div className="quantity">
                <span className="qty">1</span>
                <button className="deleteBtn" onClick={deleteItem}>X</button>
            </div>
        </div>
    )
}
