import { useEffect, useState } from "react"
import { Articles } from "./Articles";
import { pics } from "./Pictures"

export const Filter = ({nameEn, nameBg, info})=>{
    
    let [isPicked, setPick] = useState(false);
    
    function onFilter(e){
        let allElements = e.target.parentElement.parentElement.children;
        console.log(allElemen);
        setPick(!isPicked)
    }

    return (
        <li key={nameEn}className={isPicked ? 'filter picked' : 'filter'} data={nameEn} onClick={onFilter}>
            <img src={pics[nameEn]} alt={nameEn} />
            <span className='text'> {nameBg} </span>
        </li>
    )
}
