import { useState } from "react";

export const CartItems = ({info, data, deleteFromCart }) => {

    const currentElement = data.find( x=> x.name == info.name);

    const deleteItem = () => {
        deleteFromCart(currentElement)
    }
    let [value, setValue] = useState(1)

    const onChangeEvent = (e)=>{
        setValue()
    }

    return (
        <div className="item">
            <div className="title">{currentElement.name}</div>
            <div className="price">{value*currentElement.price} лв.</div>
            <div className="quantity">
            <input className="qty" type="number" value={value} onChange={onChangeEvent}/>
                <button className="deleteBtn" onClick={deleteItem}>X</button>
            </div>
        </div>
    )
}
