import brand from '../img/brand.png';
import { useEffect, useState } from "react";
import { Hamburger } from './header/Hamburger';
import { NavbarItems } from './header/NavbarItems'
import {Link} from 'react-router-dom'

export const Header = ({itemsInCartCounter}) => {
    let [ham, setHam] = useState(false)
    let [index ,setIndex] = useState(0)
    let width = window.innerWidth;
    let [shake, setShake ]=useState(false)

    useEffect(()=>{
        setIndex(itemsInCartCounter)
        setShake(true)
        setTimeout(()=>{setShake(false)},400)
    },[itemsInCartCounter])
    
    useEffect(()=>{
        if (width < 900) {
            setHam(true)
        } else {
            setHam(false)
        }
    },[])

    window.addEventListener('resize', ()=>{
        width = window.innerWidth
        if (width < 900) {
            setHam(true)
        } else {
            setHam(false)
        }
    })

    return (
        <section className="header">
            <nav className="navBar">
                <ul className='navUl'>
                    <li className='navItem'>
                        <Link to="#" className="brand" /><img src={brand} alt='logo' />
                    </li>
                    <li className="navItem">
                        <Link to='#'><i className={shake ? "fas fa-shopping-cart shake" : "fas fa-shopping-cart"}></i> {!ham && 'Количка'}<i className="index">{index}</i></Link>
                    </li>
                    {ham && <Hamburger />}
                    {!ham && <NavbarItems />}
                </ul>
            </nav>
        </section>
    )
}
