import { useState } from "react"
import { pics } from "./Pictures"

export const Filter = ({data, name})=>{

    let [isPick, setPick] = useState(false);

    function onFilter(){
        setPick = true
    }

    return (
        <li key={data} className={'filter'} data={data} onClick={onFilter}>
            <img src={pics[data]} alt={data} />
            <span className='text'> {name} </span>
        </li>
    )
}
