export const sendPurchase = (data) => {

    fetch('111https://api.emailjs.com/api/v1.0/email/send', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(),
        contentType: false,
        processData: false
    })
        .then(response => {
            console.log(data);
        if (response.ok) {
            alert('Вашата поръчка беше направена успешно!')
        } else {
            throw new Error()
        }
    })
    .catch(error => alert('Упс... Възникна грешка... Моля опитайте отново по-късно!'))
}
