import { useEffect, useState } from "react"
import { CartItems } from "./CartItems"

export const Cart = ({itemForCart, data, deleteFromCart})=>{

    let totalPrice = Array.from(itemForCart).map(x=> x.price).reduce((curr, next)=>curr+next,0)

    const getValue = ()=>{

    }

    if(itemForCart.length != 0){
        return (
            <section className="cart">
                <h4>Количка</h4>
                <div className="headers">
                    <div className="cart-item">Продукти</div>
                    <div className="cart-price">Цена</div>
                    <div className="cart-quantity">Количество</div>
                </div>
                    {Array.from(itemForCart).map((x,i) => x.isRendered && <CartItems 
                    key={i} 
                    info={x} data={data} deleteFromCart={deleteFromCart}/>)}
                <div className="total">
                    <span className="shipping">Цена за доставка: <span id="dostavka-price">1.99</span> лв.</span>
                    <span className="total-title">Обща сума:</span>
                    <span className="total-price">{(1.99 + totalPrice).toFixed(2)} лв.</span>
                </div>
            </section>
        )
    }
}
