    const asdf = data.find( x=> x.name == name);

console.log(asdf);
    const deleteItem = () => {
        deleteElement({
            id: data.objectId,
            name,
            price 
        })
    }

    return (
        <div className="item">
            <div className="title">{name}</div>
            <div className="price">{price}</div>
            <div className="quantity">
                <span className="qty">1</span>
                <button className="deleteBtn" onClick={deleteItem}>X</button>
            </div>
        </div>
    )
}
