import { useState } from "react"

export const Hamburger = () => {
    let [isActive, setIsActive] = useState(false)

    const clicked = () => {
        setIsActive(!isActive)
    }

    return (
        <>
            <button className="hamburger-menu" onClick={clicked}>
                <div>{!isActive? <i class="fa-solid fa-bars"></i> : <i class="fa-solid fa-xmark"></i>}</div>
            </button>
            {isActive && 
            <div className="hamburger-items-list">
                <div className="hamburger-item"><a href='#'>Начало</a></div>
                <div className="hamburger-item"><a href='#menu'><i className="fas fa-utensils"></i> МЕНЮ</a></div>
                <div className="hamburger-item"><a href='#'>За нас</a></div>
                <div className="hamburger-item"><a href="tel:0876442740"><i className="fas fa-phone"></i> 0876 442 740</a></div>
                <div className="hamburger-item"><a href="mailto:priateli@abv.bg"><i className="fas fa-at"></i> priateli@abv.bg</a></div>
            </div>}
        </>
    )
}
