import { CartItems } from "./CartItems"

export const Cart = ({})=>{

    let [itemForCart, setItemForCart] = useState([])

    const addToCartClickHandler = (e)=>{
      setItemForCart(oldArr => [...oldArr, e.target.parentElement.parentElement]);
    }

    if(itemForCart.length != 0){
        return (
            <section className="cart">
            <h4>Количка</h4>
            <div className="headers">
                <span className="cart-item">Продукти</span>
                <span className="cart-price">Цена</span>
                <span className="cart-quantity">Количество</span>
            </div>

            <div className="items">
                {itemForCart.map((x,i) => <CartItems key={i} data={x} />)}
            </div>
        </section>
        )
    }   
}
