import { useEffect, useState } from "react"
import { Articles } from "./Articles";
import { pics } from "./Pictures"

export const Filter = ({ nameEn, nameBg}) => {

    let [isPicked, setPick] = useState(false);

    function onFilter(e) {
        setPick(!isPicked)
    }

    return (
        <li key={nameEn} 
            className={isPicked ? 'filter picked' : 'filter'} 
            section-name={nameEn} 
            onClick={on}
        >
            <img src={pics[nameEn]} alt={nameEn} />
            <span className='text'> {nameBg} </span>
        </li>
    )
}
